import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './profile.css';
import profilePicture from './profilePicture.jpg';

function Profile() {
    const [user, setUser] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                console.log('getItem userId:')
                const userId = sessionStorage.getItem('userId'); // Используйте ключ 'userId' для получения идентификатора пользователя
                console.log(userId);
                if (!userId) {
                    throw new Error('User ID not found in sessionStorage');
                }
                const response = await axios.get(`http://localhost:8080/user/details/${userId}`, {
                    headers: {
                        Authorization: `Bearer ${userId}`,
                    },
                });
                console.log(response.data);
                setUser(response.data);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };
        fetchUserData();
    }, []);


    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <main className="main">
            <div className="profile">
                <div className="profileHeader">
                    <div className="profilePhoto">
                        <img src={profilePicture} alt="Profile" />
                    </div>
                    <div className="fullName">
                        <span>{user.firstname} {user.surname}</span>
                        <span className="createdAt">на сервисе с {user.createdAt}</span>
                    </div>
                </div>
                <div className="profileInfo">
                    <span className="personalData">Личные данные</span>
                    <div className="userInfo">
                        <div className="userInfoItem">
                            <span className="infoLabel">ФИО:</span>
                            <span className="infoValue">{user.firstname} {user.lastName}</span>
                        </div>
                        <div className="userInfoItem">
                            <span className="infoLabel">Пол:</span>
                            <span className="infoValue">{user.gender}</span>
                        </div>
                        <div className="userInfoItem">
                            <span className="infoLabel">Телефон:</span>
                            <span className="infoValue">{user.phone}</span>
                        </div>
                        <div className="userInfoItem">
                            <span className="infoLabel">Email:</span>
                            <span className="infoValue">{user.email}</span>
                        </div>
                    </div>
                    <div className="profileActions">
                        <button className="deleteButton">Удалить профиль</button>
                        <button className="logoutButton">Выйти</button>
                    </div>
                </div>
            </div>
        </main>
    );
}

export default Profile;
