import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminCategories = () => {
    const [categories, setCategories] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        try {
            const response = await axios.get('http://localhost:8080/admin/categories');
            setCategories(response.data);
            setIsLoading(false);
        } catch (error) {
            console.error('Error fetching categories:', error);
            setIsLoading(false);
        }
    };

    const handleDeleteCategory = async (categoryId) => {
        try {
            await axios.delete(`http://localhost:8080/admin/categories/${categoryId}`);
            // Обновляем список категорий после удаления
            fetchCategories();
        } catch (error) {
            console.error('Error deleting category:', error);
        }
    };

    return (
        <div>
            <h2>Admin Categories</h2>
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <ul>
                    {categories.map((category) => (
                        <li key={category.id}>
                            <div>
                                <strong>Category ID:</strong> {category.id}
                            </div>
                            <div>
                                <strong>Name:</strong> {category.name}
                            </div>
                            <div>
                                <button onClick={() => handleDeleteCategory(category.id)}>Delete</button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default AdminCategories;
