import React, {useEffect, useState} from 'react';
import axios from 'axios';
import './auth.css';
import { Link,useNavigate } from 'react-router-dom';

function RegisterForm() {
    const [firstname, setFirstName] = useState('');
    const [surname, setLastName] = useState('');
    const [phone, setPhoneNumber] = useState('');
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [gender,setGender]=useState('MALE');
    const [showModal, setShowModal] = useState(false);
    const navigate=useNavigate();

    const handleCloseModal = () => {
        setShowModal(false);
    };

    useEffect(() => {
        if (showModal) {
            setTimeout(() => {
                handleCloseModal(); // Автоматически закрывает модальное окно через 5 секунд
            }, 5000);
        }
    }, [showModal]);


    const handleSubmit = async (event) => {
        event.preventDefault();
        if (password!== confirmPassword) { // Проверяем, что пароли совпадают
            setErrorMessage('Пароли не совпадают');
            return; // Прерываем выполнение функции, если пароли не совпадают
        }
        try {
            const response = await axios.post('http://localhost:8080/auth/registration', {
                    firstname: firstname,
                    surname: surname,
                    phone: phone,
                    email: email,
                    gender:gender,
                    username: username,
                    password: password
                },
                {
                    headers: {
                        'Content-Type': "application/json"
                    }
                }
                );

            console.log('Registration successful:', response.data);
            navigate('/auth/login')
            setShowModal(true);
        } catch (error) {
            if (error.response && error.response.status === 400) {
                setErrorMessage('Такой пользователь уже существует');
            }
            console.error('Error registering:', error);
        }
    };

    return (
        <>
            <main className="main">
                <div className="auth-container">
                    <h1>Регистрация</h1>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            placeholder="Имя"
                            value={firstname}
                            onChange={(e) => setFirstName(e.target.value)}
                        />
                        <input
                            type="text"
                            placeholder="Фамилия"
                            value={surname}
                            onChange={(e) => setLastName(e.target.value)}
                        />
                        <input
                            type="text"
                            placeholder="Номер телефона"
                            value={phone}
                            onChange={(e) => setPhoneNumber(e.target.value)}
                        />
                        <input
                            type="email"
                            placeholder="Почта"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                        <div className="gender-select">
                            <label>
                                <input
                                    type="radio"
                                    value="MALE"
                                    checked={gender === 'MALE'}
                                    onChange={(e) => setGender(e.target.value)}
                                />
                                Мужчина
                            </label>
                            <label>
                                <input
                                    type="radio"
                                    value="FEMALE"
                                    checked={gender === 'FEMALE'}
                                    onChange={(e) => setGender(e.target.value)}
                                />
                                Женщина
                            </label>
                        </div>
                        <input
                            type="text"
                            placeholder="Придумайте логин"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="Придумайте пароль"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="Подтвердите пароль"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                        />
                        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
                        <button type="submit">ПРОДОЛЖИТЬ</button>
                    </form>
                    {showModal && (
                        <div className="modal-overlay">
                            <div className="modal-content">
                                <p>Вы успешно зарегистрированы</p>
                                <button onClick={handleCloseModal}>ОК</button>
                            </div>
                        </div>
                    )}
                    <p>У вас уже есть аккаунт? <Link to="/auth/login">Войти</Link></p>
                </div>
            </main>
        </>
    );
}

export default RegisterForm;
