import React, { useState, useEffect } from 'react';
import Filter from './Filter';
import axios from 'axios';

function Catalog() {
    const [services, setServices] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchServices(); // Загружаем список услуг при монтировании компонента
    }, []);

    const fetchServices = (categoryId = '', priceRange = '') => {
        setLoading(true); // Устанавливаем состояние загрузки в true перед отправкой запроса

        // Отправляем запрос на сервер для получения отфильтрованных услуг
        axios.get('http://localhost:8080/catalog', {
            params: {
                categoryId: categoryId,
                priceRange: priceRange
            }
        }).then(response => {
            setServices(response.data); // Обновляем состояние списка услуг
            setLoading(false); // Устанавливаем состояние загрузки в false после получения данных
        }).catch(error => {
            console.error('Ошибка при загрузке услуг:', error);
            setLoading(false); // Устанавливаем состояние загрузки в false в случае ошибки
        });
    };

    const handleFilterChange = ({ category, priceRange }) => {
        fetchServices(category, priceRange); // Вызываем функцию загрузки услуг с новыми параметрами фильтра
    };

    return (
        <div>
            <h2>Каталог</h2>
            <Filter onFilterChange={handleFilterChange} /> {/* Передаем функцию обработки изменений фильтра в компонент фильтра */}
            {loading ? (
                <div>Загрузка...</div>
            ) : (
                <div>
                    {/* Отображаем список услуг */}
                    <ul>
                        {services.map(service => (
                            <li key={service.id}>
                                <h3>{service.name}</h3>
                                <p>{service.description}</p>
                                <p>Цена: {service.priceMin} - {service.priceMax}</p>
                            </li>
                        ))}
                    </ul>
                    {services.length === 0 && <div>Нет результатов</div>}
                </div>
            )}
        </div>
    );
}

export default Catalog;
