import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminServices = () => {
    const [services, setServices] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetchServices();
    }, []);

    const fetchServices = async () => {
        try {
            const response = await axios.get('http://localhost:8080/admin/services');
            setServices(response.data);
            setIsLoading(false);
        } catch (error) {
            console.error('Error fetching services:', error);
            setIsLoading(false);
        }
    };

    const handleDeleteService = async (serviceId) => {
        try {
            await axios.delete(`http://localhost:8080/admin/services/${serviceId}`);
            // Обновляем список услуг после удаления
            fetchServices();
        } catch (error) {
            console.error('Error deleting service:', error);
        }
    };

    return (
        <div>
            <h2>Admin Services</h2>
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <ul>
                    {services.map((service) => (
                        <li key={service.id}>
                            <div>
                                <strong>Service ID:</strong> {service.id}
                            </div>
                            <div>
                                <strong>Name:</strong> {service.name}
                            </div>
                            <div>
                                <strong>Description:</strong> {service.description}
                            </div>
                            <div>
                                <strong>Category:</strong> {service.category.name}
                            </div>
                            <div>
                                <strong>Price Range:</strong> {service.priceMin} - {service.priceMax}
                            </div>
                            <div>
                                <button onClick={() => handleDeleteService(service.id)}>Delete</button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default AdminServices;
