import React, { useState } from 'react';
import axios from 'axios';

const Filter = ({ onFilterChange }) => {
    const [category, setCategory] = useState('');
    const [priceRange, setPriceRange] = useState('');

    const handleFilterChange = () => {
        onFilterChange({ category, priceRange });
        // Отправляем запрос на сервер с новыми значениями фильтра
        axios.get('/catalog', {
            params: {
                categoryName: category,
                priceRange: priceRange
            }
        }).then(response => {
            // Обработка ответа от сервера
            console.log(response.data);
        }).catch(error => {
            // Обработка ошибки
            console.error('Ошибка при загрузке данных:', error);
        });
    };

    return (
        <div>
            <h3>Фильтр</h3>
            <div>
                <label>Категория:</label>
                <select value={category} onChange={(e) => setCategory(e.target.value)}>
                    <option value="">Все</option>
                    <option value="Электрика">Электрика</option>
                    <option value="Сантехника">Сантехника</option>
                    <option value="Вывоз мусора">Вывоз мусора</option>
                    <option value="Ремонт и установка замков">Ремонт и установка замков</option>
                    <option value="Дезинсекция">Дезинсекция</option>
                    <option value="Прочее">Прочее</option>

                </select>
            </div>
            <div>
            <label>Цена:</label>
                <input type="text" placeholder="от" value={priceRange} onChange={(e) => setPriceRange(e.target.value)} />
            </div>
            <button onClick={handleFilterChange}>Показать</button>
        </div>
    );
};

export default Filter;
