import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../auth/AuthContext';
import axios from 'axios';

const MyOrders = () => {
    const { isAuthenticated } = useContext(AuthContext);
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchOrders = async () => {
            if (isAuthenticated) {
                try {
                    const userId = sessionStorage.getItem('userId');
                    const response = await axios.get(`http://localhost:8080/my-orders?id=${userId}`);
                    console.log(response.data)
                    setOrders(response.data);
                    setLoading(false);
                } catch (error) {
                    console.error('Failed to fetch orders', error);
                    setLoading(false);
                }
            }
        };

        fetchOrders();
    }, [isAuthenticated]);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Мои заказы</h1>
            <ul>
                {orders.map(order => (
                    <li key={order.id}>
                        <div>Заказчик: {order.user.firstname} {order.user.surname}</div>
                        <div>{order.executor.username}</div>
                        <div>Order ID: {order.id}</div>
                        <div>Description: {order.description}</div>
                        <div>Status: {order.status}</div>
                        <div>Start Date: {order.startDate}</div>
                        <div>End Date: {order.endDate}</div>
                        <div>Создан: {order.createdAt}</div>

                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MyOrders;
