import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import './profile.css';
import profilePicture from './profilePicture.jpg'
function Profile() {
    const [user, setUser] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get('http://localhost:8080/user/details', { withCredentials: true });
                setUser(response.data);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };
        fetchUserData();
    }, []);

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <main className="main">
            <div className="profile">
                <div className="profileHeader">
                    <div className="profilePhoto">
                        <img src={profilePicture} alt="Profile" />
                    </div>
                    <div className="fullName">
                        <span>{user.firstName} {user.lastName}</span>
                        <span className="createdAt">на сервисе с {user.createdAt}</span>
                    </div>
                </div>
                <div className="profileInfo">
                    <span className="personalData">Личные данные</span>
                    {/* Остальные поля профиля */}
                </div>
            </div>
        </main>
    );
}

export default Profile;
