import React, { useState, useEffect } from 'react';
import Filter from './Filter';
import '../global.css';
import axios from 'axios';
import './catalog.css';

function Catalog() {
    const [services, setServices] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchServices();
    }, []);

    const fetchServices = (categoryId = '', priceFrom = '', priceTo = '') => {
        setLoading(true);

        axios.get('http://localhost:8080/catalog', {
            params: {
                categoryId: categoryId,
                priceFrom: priceFrom,
                priceTo: priceTo
            }
        }).then(response => {
            setServices(response.data);
            setLoading(false);
        }).catch(error => {
            console.error('Ошибка при загрузке услуг:', error);
            setLoading(false);
        });
    };

    const handleFilterChange = ({ category, priceFrom, priceTo }) => {
        fetchServices(category, priceFrom, priceTo);
    };

    const arrayBufferToBase64 = (buffer) => {
        let binary = '';
        let bytes = new Uint8Array(buffer);
        let len = bytes.byteLength;
        for (let i = 0; i < len; i++) {
            binary += String.fromCharCode(bytes[i]);
        }
        return window.btoa(binary);
    };

    return (
        <main className="main">
            <div className="catalog">
                <div className="filtration">
                    <h2>Каталог</h2>
                    <Filter onFilterChange={handleFilterChange} />
                </div>
                {loading ? (
                    <div>Загрузка...</div>
                ) : (
                    <div>
                        <ul>
                            {services.map(service => (
                                <li key={service.id}>
                                    <h3>{service.name}</h3>
                                    <p>{service.description}</p>
                                    <div>
                                        <h4>Исполнитель:</h4>
                                        <p>Фамилия: {service.surname}</p>
                                        <p>Имя: {service.firstname}</p>
                                        <p>Стаж работы: {service.workExperience}</p>
                                        <p>Рейтинг: {service.rating}</p>
                                        {service.profilePicture && (
                                            <img
                                                src={`data:image/jpeg;base64,${arrayBufferToBase64(service.profilePicture)}`}
                                                alt="Profile"
                                            />
                                        )}
                                    </div>
                                </li>
                            ))}
                        </ul>
                        {services.length === 0 && <div>Нет результатов</div>}
                    </div>
                )}
            </div>
        </main>
    );
}

export default Catalog;
