import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminOrders = () => {
    const [orders, setOrders] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const response = await axios.get('http://localhost:8080/admin/orders');
            setOrders(response.data);
            setIsLoading(false);
        } catch (error) {
            console.error('Error fetching orders:', error);
            setIsLoading(false);
        }
    };

    const handleDeleteOrder = async (orderId) => {
        try {
            await axios.delete(`http://localhost:8080/admin/orders/${orderId}`);
            fetchOrders();
        } catch (error) {
            console.error('Error deleting order:', error);
        }
    };

    return (
        <div>
            <h2>Admin Orders</h2>
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <ul>
                    {orders.map((order) => (
                        <li key={order.id}>
                            <div>
                                <strong>Order ID:</strong> {order.id}
                            </div>
                            <div>
                                <strong>User:</strong> {order.user.firstname} {order.user.surname}
                            </div>
                            <div>
                                <strong>Task:</strong> {order.task.name}
                            </div>
                            <div>
                                <strong>Description:</strong> {order.description}
                            </div>
                            <div>
                                <strong>Status:</strong> {order.status}
                            </div>
                            <div>
                                <strong>Start Date:</strong> {order.startDate}
                            </div>
                            <div>
                                <strong>End Date:</strong> {order.endDate}
                            </div>
                            <div>
                                <button onClick={() => handleDeleteOrder(order.id)}>Delete</button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default AdminOrders;
